            <span class="token keyword">const</span> titleList <span class="token operator">=</span> <span class="token function">getValueListByReg</span><span class="token punctuation">(</span>htmlText<span class="token punctuation">,</span> <span class="token string">'fromPageTitle'</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">map</span><span class="token punctuation">(</span><span class="token parameter">item</span> <span class="token operator">=></span> item<span class="token punctuation">.</span><span class="token function">replace</span><span class="token punctuation">(</span><span class="token string">"&lt;strong>"</span><span class="token punctuation">,</span> <span class="token string">''</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">replace</span><span class="token punctuation">(</span><span class="token string">"&lt;\\/strong>"</span><span class="token punctuation">,</span> <span class="token string">''</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

            <span class="token comment">// 为了方便和后续json数据结合, 修改为同一个数组</span>
            <span class="token keyword">let</span> allImageUrls <span class="token operator">=</span> imageUrlList<span class="token punctuation">.</span><span class="token function">map</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token parameter">imgUrl<span class="token punctuation">,</span> index</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">(</span><span class="token punctuation">{</span>
                imgUrl<span class="token punctuation">,</span>
                <span class="token literal-property property">title</span><span class="token operator">:</span> titleList<span class="token punctuation">[</span>index<span class="token punctuation">]</span>
            <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

            <span class="token keyword">const</span> firstPageCount <span class="token operator">=</span> allImageUrls<span class="token punctuation">.</span>length<span class="token punctuation">;</span>

            <span class="token keyword">if</span> <span class="token punctuation">(</span>counts <span class="token operator">></span> firstPageCount<span class="token punctuation">)</span> <span class="token punctuation">{</span>
